INSERT INTO `sj_about` (`id`, `status`, `title`, `content`, `remark`, `updatetime`, `type`) VALUES
(1, 1, '关于保椿', '<p style="text-indent: 2em;"><span style="font-size: 14px;">保椿公司旗下核心模块，作为创新养老的驱动器，保椿信息科技公司专注于解决老人养老生活的综合服务解决方案提供商。旗下自主研发的养老生活综合服务平台 ——“一点椿APP”，是国内首家养老生活结合科技智慧养老+互联网新技术的生活平台，是对养老行业快速发展中的创新之举。</span></p><p style="text-indent: 2em;"><span style="font-size: 14px;">一点椿APP构思创立于2018年10月，团队运作中心设立于华南广州核心创意园区。作为美好养老生活服务商，保椿信息科技坚持以科技养老为第一驱动力，依托养老+互联网手段，打造以老年人生活需求为纽带的综合服务平台，为老年群体提供有尊严、有温度、有乐趣、有伙伴的养老生活方式，全方位打造一种健康、愉悦、长寿的暮年生活精神。</span></p><p style="text-indent: 2em;"><span style="font-size: 14px;">一点椿APP满足包含居家照护、机构床位、适老用品、社交娱乐、健康管理等服务产品模块，采用开放平台的模式链接行业资源，其开放创新的养老商业模式，是在深刻理解国家政策和养老服务体系特点的基础上，运用各种先进的信息技术，创建 “系统+服务+老人+终端”的智慧养老服务模式，并且涵盖了服务消费平台、机构养老、居家养老、社区日间照料、一对一上门服务等多种养老形式。同时，通过保椿旗下自营微小机构与自建护理员团队实现供给端的能力构建，确保支撑线上线下O2O的供需闭环实现，以点带面深耕区域客群；并通过跨终端的数据互联及同步，连通各中心及角色，形成一个完整的智慧管理闭环，打通“人、物、场”，实现老人与子女、服务机构、医护人员的信息交互，对老人的身体照料，安稳和日常活动进行有效监控，及时满足老人在精神、生活、健康、安全、娱乐、家庭和睦等方面的需求。</span></p><p><br/></p>', '', '2019-09-21 14:59:00', 0);
